
import pytest
from pattern import en

@pytest.mark.parametrize('value,op', [("The cat is eating (e.g., a fish). Yum.", ["The cat is eating ( e.g. , a fish ) .", "Yum ."]), ("I think I'll go to Boston.  I think I'm starting over.  I think I'll start a new life where no one knows my name.", [['I', 'think', 'I', "'ll", 'go', 'to', 'Boston', '.'], ['I', 'think', 'I', "'m", 'starting', 'over', '.'], ['I', 'think', 'I', "'ll", 'start', 'a', 'new', 'life', 'where', 'no', 'one', 'knows', 'my', 'name', '.']]), ('See Section 3).  Or Section 2).  ', ['See Section 3).', 'Or Section 2).']), ('See Section 3.)  Or Section 2.)  ', ['See Section 3.)', 'Or Section 2.)']), ('See Section 3.  Or Section 2.  ', ['See Section 3.', 'Or Section 2.']), ("I think I'll go to Boston.  I think I'm starting over.  I think I'll start a new life where no one knows my name.", [['I', 'think', 'I', "'ll", 'go', 'to', 'Boston', '.'], ['I', 'think', 'I', "'m", 'starting', 'over', '.'], ['I', 'think', 'I', "'ll", 'start', 'a', 'new', 'life', 'where', 'no', 'one', 'knows', 'my', 'name', '.']]), ('A Ukrainian separatist leader is calling on Russia to "absorb" the eastern region of Donetsk after Sunday\'s referendum on self rule. Self-declared Donetsk People\'s Republic leader Denis Pushilin urged Moscow to listen to the "will of the people". In neighbouring Luhansk, where a vote was also held, rebels declared independence. Ukraine, the EU and US have declared the referendums illegal but Russia says the results should be "implemented". Moscow has so far not commented on the call for Donetsk to become part of Russia but has appealed for dialogue between the militants and Kiev, with the participation of the Organisation for Security and Co-operation in Europe.', ['A Ukrainian separatist leader is calling on Russia to "absorb" the eastern region of Donetsk after Sunday\'s referendum on self rule.', 'Self-declared Donetsk People\'s Republic leader Denis Pushilin urged Moscow to listen to the "will of the people".', 'In neighbouring Luhansk, where a vote was also held, rebels declared independence.', 'Ukraine, the EU and US have declared the referendums illegal but Russia says the results should be "implemented".', 'Moscow has so far not commented on the call for Donetsk to become part of Russia but has appealed for dialogue between the militants and Kiev, with the participation of the Organisation for Security and Co-operation in Europe.']), ('the quick brown fox jumped over the lazy dog', ['the quick brown fox jumped over the lazy dog']), ('the quick brown fox jumped over the lazy dog. The lazy dog was not impressed.', ['the quick brown fox jumped over the lazy dog.', 'The lazy dog was not impressed.'])])
def test_tokenize(value, op):
    assert (en.tokenize(value) == op)
